import React, { useState, useEffect } from "react";
import DeleteBtn from "../components/DeleteBtn";
import Jumbotron from "../components/Jumbotron";
import API from "../utils/API";
import { Link } from "react-router-dom";
import { Col, Row, Container } from "../components/Grid";
import { List, ListItem } from "../components/List";
import { Input, TextArea, FormBtn } from "../components/Form";

function Components() {
  // Setting our component's initial state
  const [books, setBooks] = useState([]);
  const [formObject, setFormObject] = useState({});

  // Load all books and store them with setBooks
  useEffect(() => {
    loadComponents();
  }, []);

  // Loads all books and sets them to books
  function loadComponents() {
    API.getComponents()
      .then((res) => setComponents(res.data))
      .catch((err) => console.log(err));
  }

  // Deletes a book from the database with a given id, then reloads books from the db
  function deleteComponent(id) {
    API.deleteComponent(id)
      .then((res) => loadBooks())
      .catch((err) => console.log(err));
  }

  // Handles updating component state when the user types into the input field
  function handleInputChange(event) {
    const { name, value } = event.target;
    setFormObject({ ...formObject, [name]: value });
  }

  // When the form is submitted, use the API.saveBook method to save the book data
  // Then reload books from the database
  function handleFormSubmit(event) {
    event.preventDefault();
    if (formObject.title && formObject.author) {
      API.saveComponent({
        name: formObject.name,
        type: formObject.type,
        manufacturerSKU: formObject.manufacturerSKU,
        description: formObject.description,
      })
        .then((res) => loadBooks())
        .catch((err) => console.log(err));
    }
  }

  return (
    <Container fluid>
      <Row>
        <Col size="md-6">
          <Jumbotron>
            <h1>What Component are you trying to work out?</h1>
          </Jumbotron>
          <form>
            <Input
              onChange={handleInputChange}
              name="name"
              placeholder="Title (required)"
            />
            <Input
              onChange={handleInputChange}
              name="type"
              placeholder="Component Type (required)"
            />
            <TextArea
              onChange={handleInputChange}
              name="description"
              placeholder="Description (Optional)"
            />
            <FormBtn
              disabled={!(formObject.name && formObject.title)}
              onClick={handleFormSubmit}
            >
              Submit Component
            </FormBtn>
          </form>
        </Col>
        <Col size="md-6 sm-12">
          <Jumbotron>
            <h1>Saved Components</h1>
          </Jumbotron>
          {components.length ? (
            <List>
              {components.map((component) => (
                <ListItem key={component._id}>
                  <Link to={"/components/" + component._id}>
                    <strong>
                      {component.title} by {component.author}
                    </strong>
                  </Link>
                  <DeleteBtn onClick={() => deleteComponent(component._id)} />
                </ListItem>
              ))}
            </List>
          ) : (
            <h3>No Results to Display</h3>
          )}
        </Col>
      </Row>
    </Container>
  );
}

export default Components;
